function imgborder() {
  for (var i=0; i< document.images.length; i++) {
    document.images[i].style.borderStyle='inset';
  }
}

function ctrlPushed(event) {  
  if (!event) event=window.event;
  if (!event) return false;
  return event.ctrlKey;
}
