script:
	$(function() {
		function select(event, ui) {

            var ia=ui.item.children('a')[0];
            var url=ia.getAttribute('url');
            var tconfirm=ia.getAttribute('confirm');
            var target=ia.getAttribute('target');
            var confirmated=true;
            if (tconfirm) {
                if (!confirm(tconfirm) ) {
                    confimated=false;
                }
            }
            if (confirmated) {
                if (target=='finfo' && url) {
                    if (parent.finfo[famid] && parent.finfo[famid].location) {
                        parent.finfo[famid].location=url;
                    } else {
                        window.open(url,target);
                    }
                } else if (url) {
                        window.open(url,target);
                }
             }
		}
        function adjustFixed() {
            if (!isIE6) {
            var barH=$('#bar1').height();
            $('#fakeBarMenu').css("height",barH+'px');
            }
            $(".ui-menubar ul.ui-menu").css("max-height",($(window).height()-50)+"px");
        }
		$("#bar1").menubar({
			position: {
				within: $("#mainbarmenu").add(window).first()
			},
			select: select
		});
        $('#bar1 div[role="button"]').addClass("ui-button-text-only ui-button ui-widget ui-button-text-only ui-menubar-link");
        $('#bar1 div[role="button"] a').addClass("ui-button-text");
        $(window).resize(function() {
            adjustFixed()
        });
        adjustFixed()


	});
